REPL_PASSWORD=</span><span><span class="hljs-string">"<span class="hljs-variable">${REPL_PASSWORD:-replpw}</span></span></span><span>"

</span><span><span class="hljs-comment"># If already initialized, skip</span></span><span>
</span><span><span class="hljs-keyword">if</span></span><span> [ -d </span><span><span class="hljs-string">"<span class="hljs-variable">$DATA_DIR</span></span></span><span>/base" ] || [ -f </span><span><span class="hljs-string">"<span class="hljs-variable">$DATA_DIR</span></span></span><span>/PG_VERSION" ]; </span><span><span class="hljs-keyword">then</span></span><span>
  </span><span><span class="hljs-built_in">echo</span></span><span> </span><span><span class="hljs-string">"Replica data dir not empty, skipping base backup."</span></span><span>
  </span><span><span class="hljs-built_in">exit</span></span><span> 0
</span><span><span class="hljs-keyword">fi</span></span><span>

</span><span><span class="hljs-comment"># Wait for primary to be ready</span></span><span>
</span><span><span class="hljs-built_in">echo</span></span><span> </span><span><span class="hljs-string">"Waiting for primary to accept connections..."</span></span><span>
</span><span><span class="hljs-keyword">until</span></span><span> pg_isready -h </span><span><span class="hljs-variable">$PRIMARY_HOST</span></span><span> -p </span><span><span class="hljs-variable">$PRIMARY_PORT</span></span><span> -U postgres; </span><span><span class="hljs-keyword">do</span></span><span>
  </span><span><span class="hljs-built_in">sleep</span></span><span> 1
</span><span><span class="hljs-keyword">done</span></span><span>

</span><span><span class="hljs-comment"># Create .pgpass for non-interactive pg_basebackup</span></span><span>
</span><span><span class="hljs-built_in">echo</span></span><span> </span><span><span class="hljs-string">"<span class="hljs-variable">$PRIMARY_HOST</span></span></span><span>:</span><span><span class="hljs-variable">$PRIMARY_PORT</span></span><span>:*:replicator:</span><span><span class="hljs-variable">$REPL_PASSWORD</span></span><span>" &gt; /tmp/pgpass
</span><span><span class="hljs-built_in">chmod</span></span><span> 600 /tmp/pgpass
</span><span><span class="hljs-built_in">export</span></span><span> PGPASSFILE=/tmp/pgpass

</span><span><span class="hljs-comment"># Perform base backup</span></span><span>
</span><span><span class="hljs-built_in">echo</span></span><span> </span><span><span class="hljs-string">"Running pg_basebackup..."</span></span><span>
pg_basebackup -h </span><span><span class="hljs-variable">$PRIMARY_HOST</span></span><span> -p </span><span><span class="hljs-variable">$PRIMARY_PORT</span></span><span> -U </span><span><span class="hljs-variable">$REPL_USER</span></span><span> -D </span><span><span class="hljs-string">"<span class="hljs-variable">$DATA_DIR</span></span></span><span>" -Fp -Xs -P -R

</span><span><span class="hljs-comment"># -R creates standby.signal and a generated primary_conninfo in postgresql.auto.conf</span></span><span>
</span><span><span class="hljs-comment"># Ensure hot_standby is enabled for read queries</span></span><span>
</span><span><span class="hljs-built_in">cat</span></span><span> &gt;&gt; </span><span><span class="hljs-string">"<span class="hljs-variable">$DATA_DIR</span></span></span><span>/postgresql.auto.conf" &lt;&lt;<span class="hljs-string">EOF
hot_standby = on
EOF</span>

</span><span><span class="hljs-built_in">echo</span></span><span> </span><span><span class="hljs-string">"Base backup complete. Replica ready."</span></span><span>
</span></span></code></div></div></pre>
<p data-start="11569" data-end="11584"><strong data-start="11569" data-end="11583">Penjelasan</strong>:</p>
<ul data-start="11585" data-end="11865">
<li data-start="11585" data-end="11814">
<p data-start="11587" data-end="11814"><code data-start="11587" data-end="11605">pg_basebackup -R</code> membuat <code data-start="11614" data-end="11630">standby.signal</code> dan menulis <code data-start="11643" data-end="11661">primary_conninfo</code> ke <code data-start="11665" data-end="11687">postgresql.auto.conf</code> yang akan dipakai saat replica container start sehingga PostgreSQL akan otomatis mencoba terhubung ke primary sebagai replica.</p>
</li>
<li data-start="11815" data-end="11865">
<p data-start="11817" data-end="11865">Skrip menunggu <code data-start="11832" data-end="11844">pg-primary</code> siap (<code data-start="11851" data-end="11863">pg_isready</code>).</p>
</li>
</ul>
